    @mock_pers_task ||= mock_model(PersTask, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all pers_tasks as @pers_tasks" do
      PersTask.stub(:all) { [mock_pers_task] }
      get :index
      assigns(:pers_tasks).should eq([mock_pers_task])
    end
  end

  describe "GET show" do
    it "assigns the requested pers_task as @pers_task" do
      PersTask.stub(:find).with("37") { mock_pers_task }
      get :show, :id => "37"
      assigns(:pers_task).should be(mock_pers_task)
    end
  end

  describe "GET new" do
    it "assigns a new pers_task as @pers_task" do
      PersTask.stub(:new) { mock_pers_task }
      get :new
      assigns(:pers_task).should be(mock_pers_task)
    end
  end

  describe "GET edit" do
    it "assigns the requested pers_task as @pers_task" do
      PersTask.stub(:find).with("37") { mock_pers_task }
      get :edit, :id => "37"
      assigns(:pers_task).should be(mock_pers_task)
    end
  end

  describe "POST create" do
