        PersTask.stub(:new).with({'these' => 'params'}) { mock_pers_task(:save => true) }
        post :create, :pers_task => {'these' => 'params'}
        assigns(:pers_task).should be(mock_pers_task)
      end

      it "redirects to the created pers_task" do
        PersTask.stub(:new) { mock_pers_task(:save => true) }
        post :create, :pers_task => {}
        response.should redirect_to(pers_task_url(mock_pers_task))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved pers_task as @pers_task" do
        PersTask.stub(:new).with({'these' => 'params'}) { mock_pers_task(:save => false) }
        post :create, :pers_task => {'these' => 'params'}
        assigns(:pers_task).should be(mock_pers_task)
      end

      it "re-renders the 'new' template" do
        PersTask.stub(:new) { mock_pers_task(:save => false) }
        post :create, :pers_task => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested pers_task" do
        PersTask.stub(:find).with("37") { mock_pers_task }
        mock_pers_task.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :pers_task => {'these' => 'params'}
      end

      it "assigns the requested pers_task as @pers_task" do
        PersTask.stub(:find) { mock_pers_task(:update_attributes => true) }
        put :update, :id => "1"
