      it "assigns the pers_task as @pers_task" do
        PersTask.stub(:find) { mock_pers_task(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:pers_task).should be(mock_pers_task)
      end

      it "re-renders the 'edit' template" do
        PersTask.stub(:find) { mock_pers_task(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
